import React, {Component} from 'react';
import DayPicker from 'react-day-picker';
import 'react-day-picker/lib/style.css';
import './App.css';

const today = new Date();
const currentYear = today.getFullYear();
const fromMonth = new Date(currentYear, today.getMonth());
const toMonth = new Date();
toMonth.setMonth(toMonth.getMonth()+1);

// console.log(fromMonth);
class App extends Component{
  constructor(props) {
    super(props);
    this.handleDayClick = this.handleDayClick.bind(this);
    this.handleYearMonthChange = this.handleYearMonthChange.bind(this);
    this.state = {
      selectedDay: undefined,
      fromMonth: fromMonth
    };
    this.today = today;
  }

  state = {
    date: new Date(),
    month: fromMonth
  }

  handleDayClick(day, modifiers = {}) {
    let errorDiv = document.getElementById("errorMessage")

    if (modifiers.disabled) {
      errorDiv.innerHTML = "Appointment is not available for the selected day";
      return;
    }
    else{
      errorDiv.innerHTML = "Appointment available";
    }
    this.setState({
      selectedDay: modifiers.selected ? undefined : day,
    });
  }

  handleYearMonthChange(month) {
    alert(month);
    this.setState({ month:month });
  }


  render() {

    const modifiersStyles = {
      disabled:{
        backgroundColor: '#fff',
        color: '#B2B2B2'
      },
      outside: {
        backgroundColor: '#fff'
      },
      selected: {
        backgroundColor: '#003087'
      },
    };

    const getDisabledDays = (selectedMonth) => {
      let date = new Date(selectedMonth);
      const disabledDays = [];

      const excludeDateArray = ["12/3/2019", "12/13/2019","1/1/2020"];
      console.log(date.getMonth());
      console.log(new Date(this.state.month).getMonth());
      console.log(this.today.getMonth());
      console.log(this.today);
      while (date.getMonth() === new Date(this.state.month).getMonth()) {
        if(date.getMonth() === this.today.getMonth()) {
          if(date.getTime() < this.today.getTime() && date.toLocaleDateString()!=this.today.toLocaleDateString()){
            disabledDays.push(new Date(date));
          }
        }
        if(date.getDay() === 0 || date.getDay() === 6){
          disabledDays.push(new Date(date));
        }
        else if(excludeDateArray.find((excludedDate) => {
          // console.log(excludedDate + " ==> "+date.toLocaleDateString());
            return excludedDate == date.toLocaleDateString();
          })) 
        {
            disabledDays.push(new Date(date));
        }
        date.setDate(date.getDate() + 1);
      }
      // console.log(enabledDays)
      return disabledDays;
    }

    console.log(this.state.month);
    // const disabledDays = getDisabledDays(12, 2019);
     const disabledDays = getDisabledDays(this.state.month);

    const allDay = `.DayPicker-Day {
      background-color: #CBE6B1;
      color: white;
    }`;

    return (
      <div className="App">
        <div className="calendar">
          <style>{allDay}</style>
          <DayPicker
            onMonthChange={this.handleYearMonthChange}
            month={this.state.month}
            fromMonth={fromMonth}
            toMonth={toMonth}
            modifiersStyles = {modifiersStyles}
            disabledDays={disabledDays}
            onDayClick={this.handleDayClick}
            selectedDays={this.state.selectedDay}
          />
        </div>
        <h1>I'm react App</h1>
        <div id="errorMessage"></div>
      </div>
    );
  }
}

export default App;
